values (#{id,jdbcType=BIGINT}, #{purchaseNum,jdbcType=INTEGER}, #{expenseId,jdbcType=BIGINT},
  #{equipment_type_id,jdbcType=BIGINT}, #{projectId,jdbcType=BIGINT},
