mapper.select:
SELECT t1.id tid, t1.OrdersNo tNo, t1.OrdersType tOType, t1.ResourceNo tRNo, t1.ResourceType tRType, t1.Operator tOperator, t1.Address tAdd, t1.NewAddress tNewAdd, d1.id d1id, d1.DeptName pdName, d2.id d2id, d2.deptName dName, t1.UserName t1Uname, t1.Contact tContact, t1.ChargeType tChType, t1.Expenses tExpenses, t1.CreatorName, t1.CreateTime tCreateTime, t1.Status tStatus, t1.UsingNumber tUNumber FROM orders t1, department d1, department d2 WHERE t1.AccountDeptNo = d1.deptNo AND t1.UseDeptNo = d2.deptNo AND t1.CreatorName = #{creatorName}
AND t1.OrdersType = #{OrderType}
AND t1.ResourceType = #{ResourceType}
AND t1.AccountDeptNo = #{parentDept}
AND t1.UseDeptNo = #{deptNo}
AND t1.Operator = #{operator}
ORDER BY tid DESC
