mapper.select:
SELECT r.id rid, r.ResourceNo, r.ResourceType, r.ServiceSupplier, d1.isSecretary d1_isSecretary, d1.id d1id, d1.DeptName dName, d2.id d2id, d2.deptName pdName, r.UserName, r.ChargeType, r.ResourceStatus FROM resources r, department d1, department d2 WHERE r.deptNo = d1.deptNo AND d1.parentDeptNo = d2.deptNo
AND r.ResourceNo = #{ResourceNo}
AND r.UserName = #{UserName}
AND d1.parentDeptNo = #{parentDeptNo}
AND r.deptNo = #{deptNo}
AND r.ResourceType = #{ResourceType}
AND r.ServiceSupplier = #{ServiceSupplier}
