  #{createtime,jdbcType=VARCHAR}, #{lastlogin,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR},#{contact,jdbcType=VARCHAR}
  )

mapper.select:
select

from t_user
where
LoginAcct = #{loginacct,jdbcType=VARCHAR}
and
password = #{password,jdbcType=VARCHAR}

