mapper.insert:
insert into user (id, username, password,
  CreateTime, LastLogin, Status
  )
values (#{id,jdbcType=BIGINT},#{loginacct,jdbcType=VARCHAR}, #{username,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR},
  #{createtime,jdbcType=VARCHAR}, #{lastlogin,jdbcType=VARCHAR}, #{status,jdbcType=BIT}
  )

mapper.select:
select

from user
where
LoginAcct = #{loginacct,jdbcType=VARCHAR}
and
password = #{password,jdbcType=VARCHAR}

select

from user
where
LoginAcct = #{loginacct}
and
password = #{password}
